    --txt1: #ddd;
    --txt2: #bbb;
    --txt3: #555;
    --txtb: #3572B0;
    --bg1: #222;
    --bg2: #333;
    --bgb: #334;
    --bgy: #442;
    --brdrc: #444;
    --brdr: 1px solid var(--brdrc);
