}

.stash-text {
    color: var(--txt2);
    border: var(--brdr);
    box-shadow: inset 0 1px 3px var(--brdrc);
}

/* Main page panel */
.aui-page-panel {
    color: var(--txt1);
    background-color: var(--bg1);
    border: var(--brdr);
