/* Active border animation */

.waybill-list-item.waybill-list-active {
  animation: activeNow 2s ease-in-out, highlightIt 2s ease-in-out infinite;
  border: 2px solid rgba(255,0,0,0.2);
  border-radius: 20px;
  margin: 10px 0;
}
@-moz-keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@-webkit-keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@-o-keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@keyframes activeNow {
  from, to {
    background-color: #353535;
  }
  50% {
    background-color: #674b37;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    -moz-filter: blur(2px);
    -o-filter: blur(2px);
  }
}
@-moz-keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}
@-webkit-keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}
@-o-keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}
@keyframes highlightIt {
  from, to {
    border-color: rgba(255,0,0,0) !important;
  }
  50% {
    border-color: #f00 !important;
  }
}

